import React, { Component } from "react";
import "./button.css";

class Button extends Component {
  handleClick = () => {
    this.props.onClick(this.props.name);
  };
  handleClass = name => {
    let str = "";
    if (isNaN(Number(name)) && name !== ".") str = "button ops";
    else str = "button";
    if (this.props.disabled && this.props.name !== "AC")
      return str + " disabled";
    else return str;
  };
  handleKey = evt => {
    console.log(evt.key);
  };
  //  handleButton = (evt,num) => {
  //    if(!num){
  //      if(evt.target.className.search("ops")!==-1)
  //       evt.target.className = evt.target.className + " hover_og";
  //      else
  //       evt.target.className = evt.target.className + " hover_wh";
  //    }
  //     else
  //       evt.target.className = evt.target.className.substr(0,evt.target.className.length-9);
  //  }
  render() {
    console.log(typeof this.props.disabled);
    return (
      <div>
        <button
          onClick={this.handleClick}
          className={this.handleClass(this.props.name)}
          disabled={this.props.name === "AC" ? false : this.props.disabled}
          //  onMouseEnter={(evt)=>this.handleButton(evt,0)}
          //  onMouseLeave={(evt)=>this.handleButton(evt,1)}
          //onKeyPress={this.handleKey}
        >
          {this.props.name[0] === "U"
            ? this.props.name.substr(1, this.props.name.length - 1)
            : this.props.name}
        </button>
      </div>
    );
  }
}

export default Button;
