//import React from 'react';
function Calculate(obj,name){
    let obj2 = Object.assign({},obj);
  if(!isNaN(Number(name))){
      if(obj2.op1===null)
        obj2.op1 = name;
      else
        obj2.op1 = obj2.op1 + name;
  }
  else if(name!=="="){
      if(obj2.operation===null){
      obj2.op2 = obj2.op1;
      obj2.op1 = null;
      obj2.operation = name;
      }
      else{
        obj2.op2 = operations[obj2.operation](Number(obj2.op1),Number(obj2.op2));
        obj2.op1 = null;
        obj2.operation = name;  
      }
  }
  else{
     obj2.op1 = operations[obj2.operation](Number(obj2.op1),Number(obj2.op2));
     obj2.op2 = null;
     obj2.operation = null;
  }
  return obj2;
}

let operations = {
    "+" : function(num1,num2){
         return num1 + num2;
    }
}

export default Calculate;
