function updateState(obj, name) {
  if (isNaN(Number(name)) && name !== ".") {
    state.updateOperation(name, obj);
  } else {
    state.operands(name, obj);
  }
  return obj;
}

let ops = {
  "+": (num1, num2) => num1 + num2,
  "*": (num1, num2) => num1 * num2,
  "/": (num1, num2) => (num2 === 0 ? "Err" : num1 / num2),

  "-": (num1, num2) => num1 - num2,
  "%": (num1, num2) => num1 % num2,
  Usqrt: num1 => Math.sqrt(num1),
  AC: obj => {
    obj.op1 = "0";
    obj.op2 = obj.operation = null;
  },
  CE: str => str.substr(0, str.length - 1),
  calculate: (obj, name) => {
    if (!name)
      obj.op1 = "" + ops[obj.operation](Number(obj.op2), Number(obj.op1));
    else obj.op1 = "" + ops[name](Number(obj.op1));
  }
};

let state = {
  next_op: 1,
  warning: false,
  //done: false,
  operands: (name, obj) => {
    // if (!state.warning)
    if (state.next_op) {
      if (name === ".") obj.op1 = "0.";
      else obj.op1 = name;
      state.next_op = 0;
    } else {
      if (obj.op1 === 0)
        if (name === ".") obj.op1 = "0.";
        else obj.op1 = name;
      else obj.op1 = obj.op1 + name;
    }
  },
  updateOperation: (name, obj) => {
    // if (!state.warning || (state.warning && name === "AC")) {
    if (name === "AC") {
      console.log("AC");
      state.warning = false;
      ops[name](obj);
      state.next_op = 1;
    } else if (name === "=") {
      if (!state.next_op && obj.operation) {
        ops.calculate(obj);
        if (obj.op1 === "Err") state.warning = true;
      } else if (state.next_op && obj.operation) obj.op1 = obj.op2;
      obj.op2 = obj.operation = null;
      state.next_op = 1;
    } else if (name === "CE") {
      obj.op1 = ops[name](obj.op1);
      if (obj.op1 === "") obj.op1 = 0;
    } else if (name[0] === "U") obj.op1 = ops[name](obj.op1);
    else {
      if (obj.operation === null) {
        obj.operation = name;
        obj.op2 = obj.op1;
        obj.op1 = null;
        state.next_op = 1;
      } else {
        if (!state.next_op) {
          ops.calculate(obj);
          if (state.warning) {
            obj.op1 = null;
            obj.op2 = obj.operation = null;
          } else {
            obj.operation = name;
            obj.op2 = obj.op1;
          }
          state.next_op = 1;
          obj.op1 = null;
        } else {
          obj.operation = name;
          state.next_op = 1;
        }
      }
    }
    // }
  }
};

export default updateState;
