//import React from 'react';
function updateState(obj,name){
    let obj2 = Object.assign({},obj);
  if(!isNaN(Number(name))){
      if(obj2.op1===null||isNaN(Number(obj2.op1)))
        obj2.op1 = name;
      else
        obj2.op1 = obj2.op1 + name;
  }
  else if(!operations[name] && name!=='='){
      obj2.op1 = "Invalid number";
      obj2.op2 = null;
      obj2.operation = null;
  }
  else if(name!=="="){
      if(obj2.operation===null){
      obj2.op2 = obj2.op1;
      obj2.op1 = null;
      obj2.operation = name;
      }
      else{
       obj2 = calculate(obj2,name);
      }
  }
  else{
      obj2 = calculate(obj2,name);
  }
  return obj2;
}

let operations = {
    "+" : function(num1,num2){
         return num1 + num2;
    },
    "*" : function(num1,num2){
      return num1* num2;
   },
    "/" : function(num1,num2){
      if(num2===0)
        return "Err";
      return num1/num2;
   },
    "-" : function(num1,num2){
      return num1-num2;
   }
}

function calculate(obj,name){
   let obj2 = Object.assign({},obj);
   if(isNaN(Number(obj2.op1)) || isNaN(Number(obj2.op2))){
     obj2.op1 = "Invalid Number";
     obj2.op2 = null;
     obj2.operation = null;
   }
   else {
     if(name!=='='){
       obj2.op2 = operations[obj2.operation](Number(obj2.op2),Number(obj2.op1));
       obj2.op1 = null;
       obj2.operation = name;
     }
     else{
       obj2.op1 =  operations[obj2.operation](Number(obj2.op2),Number(obj2.op1));
       obj2.op2 = null;
       obj2.operation = null;
     }
     return obj2;
   }

}
export default updateState;
