import React from "react";
import { Component } from "react";
import Display from "./Display";
import Dialpad from "./Dialpad";
import updateState from "./Calculate";
class App extends Component {
  state = {
    op1: 0,
    op2: null,
    operation: null,
    warning: null
  };
  handleClick = name => this.setState(updateState(this.state, name));
  handleKey = evt => console.log(evt.key);

  render() {
    return (
      <>
        <Display data={this.state} />
        <Dialpad
          onClick={this.handleClick}
          disabled={this.state.warning ? true : false}
          // onKeyPress={this.handleKey}
        />
      </>
    );
  }
}
export default App;
