//import React from 'react';
function updateState(obj, name) {
  let obj2 = Object.assign({}, obj);
  if (isNaN(Number(name)) && name !== ".") {
    if (name === "=") {
      if (obj2.operation && obj2.op1) {
        obj2 = ops.calculate(obj2);
        if (obj2.warning){
          obj2.op2 = obj2.operation = obj2.final = null;
          obj2.op1 = 0;
        }
        else {
          obj2.final = "done";
          obj2.op2 = obj2.operation = null;
        }
      } else if (!obj2.op1 && obj2.operation) {
        obj2.op2 = obj2.operation = null;
        obj2.op1 = 0;
        obj2.final = "Done";
        obj2.warning = "missing second operand";
      }

    } else if (name === "CE") {
      obj2.op1 = null;
    } else if (name === "C") {
      obj2.op1 = obj2.op1.substr(0, obj2.op1.length - 1);
    } else if (name[0] === "U") {
      if (obj2.op1) {
        obj2 = ops.calculate(obj2, name);
        if (!obj2.op2)
          obj2.final = "Done";
      } else {
        obj2.op2 = obj2.operation = null;
        obj2.op1 = 0;
        obj2.final = "Done";
        obj2.warning = "missing operand";
      }
    } else if (obj2.operation === null) {
      obj2.op2 = obj2.op1;
      obj2.op1 = 0;
      obj2.operation = name;
    } else {
      if (obj2.op1) {
        obj2 = ops.calculate(obj2);
        if (obj2.warning) {
          obj2.op2 = obj2.final = obj2.operation = null;
          obj2.op1 = 0;
        }
        else {
          obj2.op2 = obj2.op1;
          obj2.op1 = 0;
          obj2.operation = name;
        }
      } else {
        obj2.op2 = obj2.operation = null;
        obj2.op1 = 0;
        obj2.final = "Done";
        obj2.warning = "missing second operand";
      }
    }
  } else if (!isNaN(Number(name))) {
    if (obj2.op1 === 0 || obj2.final) {
      obj2.op1 = name;
      obj2.final = 0;
      obj2.warning = null;
    } else
      obj2.op1 = obj2.op1 + name;
  } else {
    if (obj2.op1 === null)
      obj2.op1 = "0.";
    else
      obj2.op1 = obj2.op1 + ".";
  }
  return obj2;
}

let ops = {
  "+": function (num1, num2) {
    return num1 + num2;
  },
  "*": function (num1, num2) {
    return num1 * num2;
  },
  "/": function (num1, num2) {
    if (num2 === 0)
      return "Err";
    return num1 / num2;
  },
  "-": function (num1, num2) {
    return num1 - num2;
  },
  "%": function (num1, num2) {
    return num1 % num2;
  },
  "Usqrt": function (num1) {
    return Math.sqrt(num1);
  },
  "calculate": function (obj, name) {
    let obj2 = Object.assign({}, obj);
    if (!name)
      obj2.op1 = "" + ops[obj2.operation](Number(obj2.op2), Number(obj2.op1));
    else
      obj2.op1 = "" + ops[name](Number(obj2.op1));
    if (obj2.op1 === "Err") {
      obj2.warning = "Err";
    }
    return obj2;
  }
}



export default updateState;
