//import React from 'react';
function updateState(obj, name) {
  // console.log(name === "CE");
  let obj2 = Object.assign({}, obj);

  if (isNaN(Number(name)) && name !== ".") {
    obj2 = state.updateOperation(name, obj);
  } else {
    obj2 = state.operands(name, obj);
  }
  return obj2;
}

let ops = {
  "+": (num1, num2) => num1 + num2,
  "*": (num1, num2) => num1 * num2,
  "/": (num1, num2) => (num2 === 0 ? "Err" : num1 / num2),

  "-": (num1, num2) => num1 - num2,
  "%": (num1, num2) => num1 % num2,
  Usqrt: num1 => Math.sqrt(num1),
  AC: obj => {
    obj.op1 = "0";
    obj.op2 = obj.operation = null;
  },
  CE: str => str.substr(0, str.length - 1),
  calculate: (obj, name) => {
    let obj2 = Object.assign({}, obj);
    if (!name)
      obj2.op1 = "" + ops[obj2.operation](Number(obj2.op2), Number(obj2.op1));
    else obj2.op1 = "" + ops[name](Number(obj2.op1));
    return obj2;
  }
};

let state = {
  next_op: 1,
  warning: false,
  //done: false,
  operands: (name, obj) => {
    let obj2 = Object.assign({}, obj);
    if (!state.warning)
      if (state.next_op) {
        if (name === ".") obj2.op1 = "0.";
        else obj2.op1 = name;
        state.next_op = 0;
      } else {
        if (obj2.op1 === 0)
          if (name === ".") obj2.op1 = "0.";
          else obj2.op1 = name;
        else obj2.op1 = obj2.op1 + name;
      }
    return obj2;
  },
  updateOperation: (name, obj) => {
    let obj2 = Object.assign({}, obj);
    if (!state.warning || (state.warning && name === "AC")) {
      if (name === "AC") {
        state.warning = false;
        ops[name](obj2);
        state.next_op = 1;
      } else if (name === "=") {
        if (!state.next_op && obj2.operation) {
          obj2 = ops.calculate(obj2);
          if ((obj2.op1 = "Err")) state.warning = true;
        }
        obj2.op2 = obj2.operation = null;
        state.next_op = 1;
      } else if (name === "CE") {
        if (!state.next_op) {
          obj2.op1 = ops[name](obj2.op1);
          if (obj2.op1 === "") obj2.op1 = 0;
        }
      } else if (name[0] === "U") obj2.op1 = ops[name](obj2.op1);
      else {
        if (obj2.operation === null) {
          obj2.operation = name;
          obj2.op2 = obj2.op1;
          obj2.op1 = "0";
          state.next_op = 1;
        } else {
          if (!state.next_op) {
            obj2 = ops.calculate(obj2);
            if (state.warning) {
              obj2.op1 = "0";
              obj2.op2 = obj2.operation = null;
            } else {
              obj2.operation = name;
              obj2.op2 = obj2.op1;
            }
            state.next_op = 1;
            obj2.op1 = "0";
          } else {
            obj2.operation = name;
            state.next_op = 1;
          }
        }
      }
    }
    return obj2;
  }
};

export default updateState;
