//import React from 'react';
function updateState(obj,name){
    let obj2 = Object.assign({},obj);
  if(isNaN(Number(name)) && name!=="."){
      if(name==="="){
        obj2 = calculate(obj2,name);
      }
      else if(name==="CE"){
        obj2.op1 = null;
      }
      else if(name==="C"){
        obj2.op1 = obj2.op1.substr(0,obj2.op1.length-1);
      }
      else if(obj2.operation===null){
        obj2.op2 = obj2.op1;
        obj2.op1 = null;
        obj2.operation = name;
      }
      else{
        obj2 = calculate(obj2,name);
      }
  }
  else if(!isNaN(Number(name))){
    if(obj2.op1===null||obj2.final){
        obj2.op1 = name;
        obj2.final = 0;
      }
    else
        obj2.op1 = obj2.op1 + name;
  }
  else {
     if(obj2.op1===null)
        obj2.op1 = "0.";
     else
        obj2.op1 = obj2.op1 + ".";
  }
  return obj2;
}

let operations = {
    "+" : function(num1,num2){
         return num1 + num2;
    },
    "*" : function(num1,num2){
      return num1* num2;
   },
    "/" : function(num1,num2){
      if(num2===0)
        return "Err";
      return num1/num2;
   },
    "-" : function(num1,num2){
      return num1-num2;
   },
   "%" : function(num1,num2){
     return num1%num2;
   }
}

function calculate(obj,name){
   let obj2 = Object.assign({},obj);
   //console.log(obj2);
   if(isNaN(Number(obj2.op1)) || isNaN(Number(obj2.op2))){
     obj2.op1 = "Invalid Number";
     obj2.op2 = null;
     obj2.operation = null;
    // console.log("here");
   }
   else {
     if(name!=='='){
       obj2.op2 = operations[obj2.operation](Number(obj2.op2),Number(obj2.op1));
       if(isNaN(obj2.op2)){
         obj2.op1 = "Invalid argument";
         obj2.op2 = null;
         obj2.operation = null;
         obj2.final = 1;
       }
      else{
       obj2.op1 = null;
       obj2.operation = name;
       obj2.final = 0;
      }
     }
     else{
       obj2.op1 = "" +  operations[obj2.operation](Number(obj2.op2),Number(obj2.op1));
       obj2.op2 = null;
       obj2.operation = null;
       obj2.final = 1;
     }
   }
   return obj2;
}


export default updateState;
