import React from "react";
import { Component } from "react";
import Display from "./Display";
import Dialpad from "./Dialpad";
import updateState from "./Calculate";
class App extends Component {
  state = {
    op1: null,
    op2: null,
    operation: null,
    key: ""
  };

  validKeys = [
    "1",
    "2",
    "3",
    "4",
    "5",
    "6",
    "7",
    "8",
    "9",
    "0",
    ".",
    "+",
    "/",
    "*",
    "-",
    "=",
    "Backspace",
    "Delete",
    "Enter"
  ];
  handleClick = name => {
    if (this.state.key !== "Enter")
      this.setState(updateState(this.state, name));
  };
  handleKeyDown = event => {
    let index = this.validKeys.indexOf(event.key);
    if (index !== -1) {
      if (this.state.op1 !== "Err" || event.key === "Delete") {
        this.setState(updateState(this.state, this.validKeys[index]));
        this.setState(() => ({ key: this.validKeys[index] }));
      } else {
        if (this.state.key) this.setState(() => ({ key: "" }));
      }
      // let updateTimer = function() {
      //
      //   this.timeOutActive.active = false;
      //   this.timeOutActive.id = "";
      // }.bind(this);

      // if (this.timeOutActive.active) {
      //   clearTimeout(this.timeOutActive.id);
      // }
      // let id = setTimeout(updateTimer, 100);
      // this.timeOutActive.active = true;
      // this.timeOutActive.id = id;
      // let key = event.key;
      //
    }
  };

  handleKeyUp = event => {
    this.setState(() => ({ key: "" }));
  };

  render() {
    return (
      <div
        onKeyDown={this.handleKeyDown}
        onKeyUp={this.handleKeyUp}
        tabIndex={0}
      >
        <Display data={this.state} />
        <Dialpad
          onClick={this.handleClick}
          disabled={this.state.op1}
          keyPressed={this.state.key}
        />
      </div>
    );
  }
}
export default App;
