  state = {
    op1: null,
    op2: null,
    operation: null,
    warning: null,
    final: 0
  }
  handleClick = (name) => this.setState(updateState(this.state, name));

  render() {
    return ( <
      >
      <
      Display data = {
        this.state.op1
      }
      warning = {
        this.state.warning
      }
      history = {
        this.state.op2
      }
      /> <
      Dialpad onClick = {
        this.handleClick
      }
      /> <
      />);
    };
  };
