import React,{Component} from "react";
import './button.css';

class Button extends Component{
   handleClick = () => {
       this.props.onClick(this.props.name);
   }
   handleClass = (name) => {
       if(isNaN(Number(name)) && name!==".")
         return "button ops";
       else
         return "button";
   }
   handleButton = (evt,num) => {
     if(!num)
        evt.target.className = evt.target.className + " hover";
      else
        evt.target.className = evt.target.className.substr(0,evt.target.className.length-6);
   }
   render(){
       return (
           <div>
             <button onClick={this.handleClick}
                     className={this.handleClass(this.props.name)}
                     onMouseEnter={(evt)=>this.handleButton(evt,0)}
                     onMouseLeave={(evt)=>this.handleButton(evt,1)}>
                    {this.props.name[0]==="U"?
                    this.props.name.substr(1,this.props.name.length-1):this.props.name
                }</button>
           </div>
       );
   }
}


export default Button;
