import React,{Component} from "react";
import './button.css';
class Button extends Component{
   handleClick = () => {
       this.props.onClick(this.props.name);
   }
   render(){
       return (
           <div>
             <button onClick={this.handleClick} className="button">{this.props.name}</button>
           </div>
       );
   }
}


export default Button;
