import React from 'react';
function Calculate(obj,name){
    let obj2 = Object.assign({},obj);
  if(obj2.operation===null){
      if(!isNaN(Number(name))){
          if(obj2.op1===null)
            obj2.op1 = name;
          else
            obj2.op1 = obj2.op1 + name;
      }
  }
  return obj2;
}

export default Calculate;
