import React from "react";
import { Component } from "react";
import Display from "./Display";
import Dialpad from "./Dialpad";
import updateState from "./Calculate";
class App extends Component {
  state = {
    op1: null,
    op2: null,
    operation: null
    // key: ""
  };
  validKeys = [
    "1",
    "2",
    "3",
    "4",
    "5",
    "6",
    "7",
    "8",
    "9",
    "0",
    ".",
    "+",
    "/",
    "*",
    "-",
    "="
  ];
  handleClick = name => this.setState(updateState(this.state, name));
  handleKeyDown = event => {
    let index = this.validKeys.indexOf(event.key);
    if (index !== -1) {
      this.setState(updateState(this.state, event.key));
      // let key = event.key;
      // this.setState(() => ({ key: key }));
    }
  };

  render() {
    return (
      <div
        onKeyDown={this.handleKeyDown}
        onKeyPress={this.handleKeyUp}
        tabIndex={0}
      >
        <Display data={this.state} />
        <Dialpad
          onClick={this.handleClick}
          disabled={this.state.op1}
          //keyPressed={this.state.key}
        />
      </div>
    );
  }
}
export default App;
