import React from "react";
import { Component } from "react";
import Display from "./Display";
import Dialpad from "./Dialpad";
import updateState from "./Calculate";
class App extends Component {
  state = {
    op1: null,
    op2: null,
    operation: null
  };
  validKeys = ["1", "2", "3", "4"];
  handleClick = name => this.setState(updateState(this.state, name));
  handleKeyDown = event => {
    let index = this.validKeys.indexOf(event.key);
    if (index !== -1) this.setState(updateState(this.state, event.key));
  };
  handleKeyUp = event => {
    let index = this.validKeys.indexOf(event.key);
    if (index !== -1) this.setState(updateState(this.state, event.key));
  };
  render() {
    return (
      <div
        onKeyDown={this.handleKeyDown}
        onKeyPress={this.handleKeyUp}
        tabIndex={0}
      >
        <Display data={this.state} />
        <Dialpad onClick={this.handleClick} disabled={this.state.op1} />
      </div>
    );
  }
}
export default App;
